#include "gmock/gmock.h"
#include "gtest/gtest.h"
#include "main.h"

class CF1398C_CountGoodSubArraysTest
    : public testing::TestWithParam<std::pair<std::string, long long>> {};

TEST_P(CF1398C_CountGoodSubArraysTest, CountGoodSubArrays) {
  auto array_string = GetParam().first;
  auto expected = GetParam().second;
  auto result = CountGoodSubArrays(array_string);
  EXPECT_EQ(result, expected);
}

// clang-format off
INSTANTIATE_TEST_CASE_P(
    CF1398C_CountGoodSubArraysTests, CF1398C_CountGoodSubArraysTest,
    ::testing::Values(std::make_pair(std::string{"0"}, 0),
                      std::make_pair(std::string{"1"}, 1),
                      std::make_pair(std::string{"120"}, 3),
                      std::make_pair(std::string{"11011"}, 6),
                      std::make_pair(std::string{"600005"}, 1),
                      std::make_pair(std::string{"11111"}, 15),
                      std::make_pair(std::string{""},
                       50005000 )));
// clang-format on

int main(int argc, char** argv) {
  ::testing::InitGoogleTest(&argc, argv);
  return RUN_ALL_TESTS();
}